  )
values (#{threadId,jdbcType=BIGINT}, #{variableName,jdbcType=VARCHAR}, #{variableValue,jdbcType=VARCHAR}
  )

mapper.update:

update status_by_thread
set VARIABLE_VALUE = #{variableValue,jdbcType=VARCHAR}
